BEGIN TRANSACTION;
CREATE TABLE IF NOT EXISTS "users" (
	"id"	INTEGER,
	"username"	text,
	"email"	text UNIQUE,
	"password"	text,
	"createdAt"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	"updatedAt"	text,
	CONSTRAINT "email_unique" UNIQUE("email"),
	PRIMARY KEY("id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "messages" (
	"msg_id"	INTEGER,
	"name"	text,
	"email"	text,
	"msg"	text,
	"created_at"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	CONSTRAINT "msg_id_unique" UNIQUE("msg_id"),
	PRIMARY KEY("msg_id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "translations" (
	"translation_id"	INTEGER,
	"de_id"	text,
	"eng_id"	text,
	"item_type"	text,
	CONSTRAINT "translation_id_unique" UNIQUE("translation_id"),
	PRIMARY KEY("translation_id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "posts" (
	"post_id"	INTEGER,
	"page_id"	INTEGER,
	"title"	text,
	"content"	text,
	"type"	text,
	"ord"	INTEGER,
	"language"	TEXT,
	CONSTRAINT "post_id_unique" UNIQUE("post_id"),
	PRIMARY KEY("post_id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "bookings" (
	"booking_id"	INTEGER,
	"first_name"	text,
	"last_name"	text,
	"email"	text,
	"telephone"	text,
	"room"	text,
	"guest_count"	text,
	"arrival"	text,
	"departure"	text,
	"notes"	text,
	"created_at"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	CONSTRAINT "booking_id_unique" UNIQUE("booking_id"),
	PRIMARY KEY("booking_id" AUTOINCREMENT)
);
CREATE TABLE IF NOT EXISTS "pages" (
	"page_id"	INTEGER,
	"title"	text,
	"link"	text UNIQUE,
	"ord"	INTEGER,
	"createdAt"	DATETIME DEFAULT CURRENT_TIMESTAMP,
	"language"	TEXT,
	CONSTRAINT "link_unique" UNIQUE("link"),
	PRIMARY KEY("page_id" AUTOINCREMENT)
);
COMMIT;
